import React, { createContext, useReducer } from 'react';

// Definir los valores iniciales del estado
const initialState = {
  theme: 'claro',
};

// Definir los posibles tipos de acciones
const actionTypes = {
  CAMBIAR_TEMA: 'CAMBIAR_TEMA',
};

// Crear el reducer para manejar las acciones
const themeReducer = (state, action) => {
  switch (action.type) {
    case actionTypes.CAMBIAR_TEMA:
      return { ...state, theme: action.payload };
    default:
      return state;
  }
};

// Crear el contexto
export const ThemeContext = createContext();

// Crear el proveedor del contexto
export const ThemeProvider = ({ children }) => {
  const [state, dispatch] = useReducer(themeReducer, initialState);

  // Función para cambiar el tema
  const cambiarTema = (nuevoTema) => {
    dispatch({ type: actionTypes.CAMBIAR_TEMA, payload: nuevoTema });
  };

  return (
    <ThemeContext.Provider value={{ theme: state.theme, cambiarTema }}>
      {children}
    </ThemeContext.Provider>
  );
};
