import React, { createContext, useState, useEffect, useReducer } from 'react';

import axios from 'axios';

export const DentistContext = createContext();

const DentistContextProvider = (props) => {

  
  const [dentists, setDentists] = useState([]);
  const [favorites, dispatch] = useReducer(favoritesReducer, []);
  const [dentista, setDentista] = useState(null);
  const [favoritos, setFavoritos] = useState([]);

  useEffect(() => {

    fetchData();
    
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get(`https://jsonplaceholder.typicode.com/users`); 
      setDentists(response.data);
    } catch (error) {
      console.error('Error fetching data from API:', error);
    }
  };





  

  return (
    <DentistContext.Provider value={{ dentists, dentista, favorites, addToFavorites, removeFromFavorites }}>
      {props.children}
    </DentistContext.Provider>
  );
}

export default DentistContextProvider;
