import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom';
import { DentistContext } from '../Components/utils/DentistContext';

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Detail = () => {

  
  const { id } = useParams();
  const { dentistbyId } = useContext(DentistContext);
  const [dentist, setDentist] = useState([]);
   

  useEffect(() => {

    setDentist(dentistbyId(id))

  }, []);
  
  


  return (
    <>
      <h1>id: {id}</h1>
      <table className="tabla-dentistas">
      <thead>
        <tr>
          <th>Nombre</th>
          <th>Tel√©fono</th>
          <th>Sitio Web</th>
        </tr>
      </thead>
      <tbody>
        {dentist.map((dentista, index) => (
          <tr key={index}>
            <td>{dentist.nombre}</td>
            <td>{dentist.telefono}</td>
            <td>
              <a href={dentista.sitioWeb} target="_blank" rel="noopener noreferrer">
                {dentista.sitioWeb}
              </a>
            </td>
          </tr>
        ))}
      </tbody>
    </table>



      {/* aqui deberan renderizar la informacion en detalle de un user en especifico */}
      {/* Deberan mostrar el name - email - phone - website por cada user en especifico */}
    </>
  )
}

export default Detail
