import React, {useContext} from "react";
import dentistImage from '../images/doctor.jpg';
import { Link } from 'react-router-dom';
import { DentistContext } from '../Components/utils/DentistContext';

const Card = ({ name, username, id }) => {

  const {  addData  } = useContext(DentistContext);
  

  const addFav = (e)=>{

    e.preventDefault();
    if (name.trim() !== '') {
      addData(id, name, username);
      
    }

    

  }
 
  return (

    
    <div className="card" >
       <Link to={`/dentist/${id}`}>
        <div>
          <img  src={dentistImage} alt="Dentist" className="card-image"/>
        </div>
        <p>{name}</p>
        <p>{username}</p>
      

        {/* En cada card deberan mostrar en name - username y el id */}

        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}

        </Link>
        <button onClick={addFav} className="favButton"><FontAwesomeIcon icon={faStar} /></button>
    </div>
    
  );
};

export default Card;
